import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, ResponsiveContainer } from 'recharts';

const HeatmapChart = ({ data }) => {
  const assessments = ['theory tests total (real)', 'practical tests total (real)', 'homework total (real)', 'project total (real)', 'engagement total (real)'];

  const calculateCorrelation = (x, y) => {
    const xMean = x.reduce((a, b) => a + b) / x.length;
    const yMean = y.reduce((a, b) => a + b) / y.length;
    const numerator = x.reduce((sum, xi, i) => sum + (xi - xMean) * (y[i] - yMean), 0);
    const denominator = Math.sqrt(
      x.reduce((sum, xi) => sum + Math.pow(xi - xMean, 2), 0) *
      y.reduce((sum, yi) => sum + Math.pow(yi - yMean, 2), 0)
    );
    return numerator / denominator;
  };

  const correlationData = assessments.flatMap((assessment1, i) =>
    assessments.map((assessment2, j) => {
      if (i === j) return { x: i, y: j, z: 1 };
      const x = data.map(student => parseFloat(student[assessment1]));
      const y = data.map(student => parseFloat(student[assessment2]));
      return {
        x: i,
        y: j,
        z: calculateCorrelation(x, y)
      };
    })
  );

  const colorScale = (value) => {
    const hue = ((1 - value) * 120).toString(10);
    return ["hsl(", hue, ",100%,50%)"].join("");
  };

  return (
    <ResponsiveContainer width="100%" height={400}>
      <ScatterChart
        margin={{
          top: 20,
          right: 20,
          bottom: 20,
          left: 20,
        }}
      >
        <XAxis type="number" dataKey="x" name="x" tick={false} />
        <YAxis type="number" dataKey="y" name="y" tick={false} />
        <ZAxis type="number" dataKey="z" range={[0, 400]} />
        <Tooltip 
          cursor={{ strokeDasharray: '3 3' }}
          content={({ payload }) => {
            if (payload && payload.length) {
              const { x, y, z } = payload[0].payload;
              return (
                <div style={{ backgroundColor: 'white', padding: '5px', border: '1px solid #ccc' }}>
                  <p>{`${assessments[x]} vs ${assessments[y]}`}</p>
                  <p>{`Correlation: ${z.toFixed(2)}`}</p>
                </div>
              );
            }
            return null;
          }}
        />
        <Scatter
          data={correlationData}
          fill={(entry) => colorScale(Math.abs(entry.z))}
        />
      </ScatterChart>
    </ResponsiveContainer>
  );
};

export default HeatmapChart;
