import React, { useState, useEffect } from 'react';
import GradeDistributionChart from './components/Charts/GradeDistributionChart';
import GradeDistributionPieChart from './components/Charts/GradeDistributionPieChart';
import FileUploader from './components/FileUploader';

const App = () => {
  const [files, setFiles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleDataReceived = (newData) => {
    if (newData && newData.file_name && newData.grade_distribution) {
      setFiles(prevFiles => [...prevFiles, { 
        name: newData.file_name, 
        data: newData.grade_distribution 
      }]);
    } else {
      setError('Received invalid data format from the server.');
    }
  };

  const fetchBarChartData = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('/charts/barChart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ table_name: 'students' }),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch bar chart data');
      }

      const data = await response.json();
      if (data.data) {
        setFiles(prevFiles => [...prevFiles, { name: 'Database Data', data: data.data }]);
      } else {
        setError('Received invalid data format from the server.');
      }
    } catch (error) {
      setError(`Error fetching bar chart data: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchBarChartData();
  }, []);

  if (isLoading) {
    return <div>Loading data...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="App">
      <h1 style={{ fontSize: '32px', fontWeight: 'bold', textAlign: 'center', margin: '20px 0' }}>Grade Distribution Analysis</h1>
      <FileUploader onDataReceived={handleDataReceived} />
      {files.length > 0 ? (
        <>
          <GradeDistributionChart files={files} />
          <GradeDistributionPieChart files={files} />
        </>
      ) : (
        <div>No data available. Please upload a file or wait for data to load.</div>
      )}
    </div>
  );
};

export default App;
